import styled from "styled-components";


export const Container = styled.div`
    width: 550px;
    margin: 0 auto;
    padding: 20px;
    background: ${p => p.theme.colors.background};
`;

export const Title = styled.h2`
    font-size: 30px;
    font-weight: 700;
    text-align: center;
    margin-bottom: 15px;
`;
